import React,{Component} from 'react'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false
        }
    }
    change = () => {
        this.setState(({done})=>({
            done:!done
        }))
    }


    render() {
        const {title} = this.props;
        let {done} = this.state;
        return(
            <li onClick={this.change}><input type="checkbox"/>{title}</li>
        )
    }
}
