import React,{Component} from 'react'
import TodoCard from './todo/todo-card'

class App extends Component{
  constructor() {
    super();
    this.state = [
      {title:'Read book', id:Date.now()},
      {title:'Products', id:Date.now()},
    ];
  }

  updateState(state) {
    this.setState(state);
  }

  addCard(setTitle) {
    const newCard = { title:setTitle}
    this.setState(...this.state,newCard)
  }

  render() {
    return (
      <div className="col-12 alert-warning row">
        {this.state.map(item => {
          return (
            <TodoCard cardTitle={item.title} cardId = {item.id}></TodoCard>
          )
        })}

      </div>
    );
  }
}

export default App;
