import React from "react";
import { ReactSortable } from "react-sortablejs";
import TodoItem from './todo-item'
import TodoInput from './todo-input'

const TodoList  = (props) => {


    return(
            <>

                <ReactSortable list={props.todoitems} setList={props.updateTodosHandle}>{
                    props.todoitems.map((element, index) => {
                        const {id, ...elem} = element;
                        return(
                            <div className="alert alert-secondary row">
                                <TodoItem {...elem}></TodoItem>
                                <i class="text-right fa fa-minus-circle col-2" onClick={props.deleteTodosItem} title="delete"/>
                            </div>
                        )
                    })
                }</ReactSortable>
                <TodoInput addTodoItem = {props.addTodoItem}></TodoInput>
            </>
    )
}
export default TodoList;
