import React,{Component, useState} from 'react';
import {App} from './itemModal'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false,
            modalShow:false
        }
    }
    setModalShow = () => {
        this.setState(({modalShow}) => ({
            modalShow:!modalShow
        }))
    }
    isDone = () => {
        this.setState(({done})=>({
            done:!done
        }))  
    }

    render() {
        const {modalShow} = this.state;
        let style = this.state.done?{textDecorationLine: 'line-through'}:{};
        let {title} = this.props;

        return(
                 <>
                    <div className="col-10"  style ={style}>
                        <input type="checkbox" className="form-check-input" checked={this.state.done} onChange={this.isDone}/>
                        <Modal  variant="primary"
                                style={{cursor:"zoom-in"}}
                                title={title.length > 20 ? title.substr(0, 20)+'...' : title}>
                        </Modal>
                    </div>
                 </>
        )
    }
}

function MyVerticallyCenteredModal(props) {
    return (
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Modal heading
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>Centered Modal</h4>
          <p>
            Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
            dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
            consectetur ac, vestibulum at eros.
          </p>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }

  function Modal() {
    const [modalShow, setModalShow] = React.useState(false);
    const {title} = this.props;
    return (
      <>
        <small variant="primary" onClick={() => setModalShow(true)}>
          {title}
        </small>

        <MyVerticallyCenteredModal
          show={modalShow}
          onHide={() => setModalShow(false)}
        />
      </>
    );
  }
