import React,{Component} from 'react'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false
        }
    }
    change = (elem) => {
        this.setState(({done})=>({
            done:!done
        }))
        console.log(elem.target);

    }


    render() {
        let {title} = this.props;
        return(
            <li onClick={this.change}> <input type="checkbox" checked={this.state.done} onChange={this.change}/>{title}</li>
        )
    }
}
