import React, {useState} from 'react';
import './item-modal.css'
import '../../node_modules/bootstrap/dist/css/bootstrap.css';
import '../../node_modules/bootstrap/dist/css/bootstrap.css';

const ItemModal = ({show, handleClose, children, description}) => {

  let [thisDescription, changeDescription] = useState(description);
  let [toInput, setToInput] = useState(false);

  const showHideClassName = show?"modal display-block" : "modal display-none";

  const Tag = toInput ? 
      <input className="alert alert-outline-secondary border-0 p-0 pl-1 col-8" onBlur={()=> setToInput(false)} value={thisDescription} placeholder={"input ..."} onChange={e => changeDescription(e.target.value)} autoFocus></input>
      : <p className="col-12" onClick={()=>setToInput(true)} placeholder='paragraph'> {thisDescription||'p tag'} </p>;
  return (
    <div className={showHideClassName}>
      <div className="col-6 offset-3 alert alert-light d-flex justify-content-center pl-5 pr-5">
        <section className="col-12">
          <h3 className="text-center">{children}</h3>
          <hr></hr>
            <div className="row ml-3">
              <small className="col-12">Description: </small>
              {Tag}
            </div>
          <hr></hr>
          <button type="button" className="btn btn-outline-danger justify-content-right" onClick={handleClose}>
            close window
          </button>
        </section>
      </div>
    </div>
  );
};




export {ItemModal};
