import React, { FC, useState } from "react";
import { ReactSortable } from "react-sortablejs-typescript";

interface ItemType {
    id: string;
    title: string;
}

export default TodoList = ({props}) => {
    const [state, setState] = useState<ItemType>([...props]);
    const todos = () => { return(
        <ReactSortable list={state} setList={setState}>{
            props.map(element => {
                const {id, ...elem} = element;
                return(
                    <li style ={{listStyleType:"none"}}key={id} className="pl-5">
                        <TodoItem {...elem}></TodoItem>
                    </li>
                )
            })
        }</ReactSortable>
    )}
    return (
            <ul className="alert alert-warning pl-3">
                {todos}
            </ul>
    )
}
