import React,{Component} from 'react';
import {ItemModal} from './itemModal'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false,
            modalShow:false
        }
    }
    setModalShow () {
        this.setState(({modalShow}) => ({
            modalShow:!modalShow
        }))
    }
    isDone = () => {
        this.setState(({done})=>({
            done:!done
        }))  
    }

    modalShow = () => {
        this.setModalShow();
        console.log('yess');
    }

    render() {
        let style = this.state.done?{textDecorationLine: 'line-through'}:{};
        let {title} = this.props; 
        return(
                 <>
                    <div className="col-10"  style ={style}>
                        <input type="checkbox" className="form-check-input" checked={this.state.done} onChange={this.isDone}/>
                        <ItemModal show={this.state.modalShow} handleClose={this.setModalShow}>
                            <p>Modal</p>
                        </ItemModal>
                        <small  
                            variant="primary"
                            style={{cursor:"zoom-in"}}
                            onClick={this.modalShow}>
                                {title.length > 20 ? title.substr(0, 20)+'...' : title}
                        </small>
                    </div>
                 </>
        )
    }
}
