import React,{Component} from 'react'
import TodoList from './todo-list'

export default class TodoCard extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todoitems: []

        };
        this.addTodoItem = this.addTodoItem.bind(this);
    }

    addTodoItem(title) {
      const newTodo = {
        title:title,
        done:false,
        id: Date.now(),
        chosen:false,
        selected: false
      };
      this.setState({todoitems:[...this.state.todoitems, newTodo]});
    }

    updateTodo = (todoitems) => {
      this.setState({todoitems});
    }

    render() {
        return (
            <div className="alert alert-info mr-3 pr-3 pl-3">
                <TodoList title={this.props.cardTitle} changeTitleofcard={this.props.changeTitleofcard} addTodoItem = {this.addTodoItem}
                todos = {this.state.todoitems} updateTodosHandle={this.updateTodo} key = {this.props.key}></TodoList>
            </div>
        )
    }
}
