import React,{Component} from 'react'
import TodoList from './todo-list'

export default class TodoCard extends Component {
    constructor(props) {
        super(props);
        this.state = {
            readBook: [
                {title:'Learn Js', done:false, id:'alkdj'},
                {title:'Repeat react tutorial', done:false, id:'kdks'},
                {title:'Js documentation', done:false, id:'lakdi'},
            ],
            products: [
                {title:'carrot', done:false, id:'carrot'},
                {title:'apple', done:false, id:'apple'},
                {title:'orange', done:false, id:'orange'},
            ]
        };
        this.addTodoItem = this.addTodoItem.bind(this);
    }

    getList = () => {
      for(let [key,  value] of Object.entries(this.state)) {
        if(key === this.props.cardId) {
          console.log('Diiiiiyaz');
        }
      }
    }

    addTodoItem(title){
      const newTodo = {
        title:title,
        done:false,
        id: Date.now()
      };
      this.setState([...this.todos, newTodo]);
    }

    updateTodo = (readBook) => {
      this.setState({readBook});
    }

    render() {
        return (
            <div className="alert alert-info mr-3 pr-3 pl-3">
                <TodoList title={this.props.cardTitle} addTodoItem = {this.addTodoItem}
                todos = {this.state.readBook} updateTodosHandle={this.updateTodo}></TodoList>
            </div>
        )
    }
}
