import React, {useState} from "react";
import { ReactSortable } from "react-sortablejs";
import TodoItem from './todo-item'
import TodoInput from './todo-input'

const TodoList  = ({todoitems, updateTodosHandle,deleteTodosItem,addTodoItem}) => {
const [action, setAction] = useState('d-none');

    return(
            <>

                <ReactSortable list={todoitems} setList={updateTodosHandle}>{
                    todoitems.map((element, index) => {
                        const {key, ...elem} = element;
                        return(
                            <div className="alert alert-light pt-1 pb-1 border-0 row text-dark shadow" style={{marginBottom:"0.3rem"}} key={index}>
                                <TodoItem {...elem} index={index}></TodoItem>
                                <i className="text-right fa fa-minus-circle col-2 pt-1 cursor-pointer" onClick={()=>setAction('show')} title="delete todo"
                                    style={{position:'relative'}}/>
                                <div className={action} style={{position:'absolute', left:'100%'}}>
                                    <ul >
                                        <li className="alert fa fa-minus-circle cursor-pointer" onClick={()=>deleteTodosItem(index)}> delete item </li>
                                        <li>second</li>
                                        <li>thirth</li>
                                    </ul>
                                </div>
                            </div>
                        )
                    })
                }</ReactSortable>
                <TodoInput addTodoItem = {addTodoItem}></TodoInput>
            </>
    )
}
export default TodoList;
